<?phpnamespace backend\controllers;use Yii;use yii\filters\VerbFilter;class BackendController extends \soft\web\SController{    /**     * @inheritDoc     */    public function behaviors()    {        return [            'access' => [                'class' => \yii\filters\AccessControl::className(),                'rules' => [                    [                        'allow' => true,                        'roles' => ['admin'],                    ],                ],            ],            'verbs' => [                'class' => VerbFilter::className(),                'actions' => [                    'delete' => ['POST'],                    'bulk-delete' => ['POST'],                ],            ],        ];    }}