<?phpnamespace backend\modules\postmanager\controllers;use backend\controllers\BackendController;use Yii;use backend\modules\postmanager\models\PostCategory;use backend\modules\postmanager\models\search\PostCategorySearch;use yii\web\Controller;class PostCategoryController extends BackendController{    public function actionIndex()    {        $searchModel = new PostCategorySearch();        $dataProvider = $searchModel->search(Yii::$app->request->queryParams);        return $this->render('index', [            'searchModel' => $searchModel,            'dataProvider' => $dataProvider,        ]);    }    public function actionView($id)    {        return $this->render('view', [            'model' =>  PostCategory::findModel($id),        ]);    }    public function actionCreate()    {        $model = new PostCategory();        if ($model->loadSave()) {            return $this->redirect(['index']);        }        return $this->render('create', compact('model'));    }    public function actionUpdate($id)    {        $model = PostCategory::findModel($id);        if ($model->loadSave()) {            return $this->redirect(['index']);        }        return $this->render('update', [            'model' => $model,        ]);    }}