<?phpnamespace backend\modules\postmanager\controllers;use Yii;use backend\controllers\BackendController;use backend\modules\postmanager\models\Post;use backend\modules\postmanager\models\search\PostSearch;/** * PostController implements the CRUD actions for Post model. */class PostController extends BackendController{    public function actions()    {        return [            'upload-image' => [                'class' => 'odilov\cropper\UploadAction',                'url' => "/uploads/post",                'path' => '@frontend/web/uploads/post',                'jpegQuality' => 75,            ]        ];    }    public function actionIndex()    {        $searchModel = new PostSearch();        $dataProvider = $searchModel->search(Yii::$app->request->queryParams);        return $this->render('index', [            'searchModel' => $searchModel,            'dataProvider' => $dataProvider,        ]);    }    public function actionView($id)    {        return $this->render('view', [            'model' => Post::findModel($id),        ]);    }    public function actionCreate()    {        $model = new Post();        $model->publishedField = date("Y-m-d H:i");        if ($model->load(Yii::$app->request->post()) ) {            $model->published_at = strtotime($model->publishedField);            if($model->save()){                return $this->redirect(['view', 'id' => $model->id]);            }        }        return $this->render('create', [            'model' => $model,        ]);    }    public function actionUpdate($id)    {        $model = Post::findModel($id);        $model->publishedField = date("Y-m-d H:i", $model->published_at);        if ($model->load(Yii::$app->request->post()) ) {            $model->published_at = strtotime($model->publishedField);            if($model->save()){                return $this->redirect(['view', 'id' => $model->id]);            }        }        return $this->render('update', [            'model' => $model,        ]);    }    public function actionDelete($id)    {        Post::findModel($id)->delete();        return $this->redirect(['index']);    }}