<?phpnamespace backend\modules\translationmanager;use Yii;use yii\helpers\ArrayHelper;use yii\helpers\Html;/** * translation module definition class */class TranslationManager extends \yii\base\Module{    /**     * @var array Array of languages     */    public $languages = ['uz', 'ru'];    public $grid_column = [];    /**     * @inheritdoc     */    public $controllerNamespace = 'backend\modules\translationmanager\controllers';    /**     * @inheritdoc     */    public function init()    {        parent::init();        if (isset(Yii::$app->params['languages'])) {            $langs = Yii::$app->site->languages();            if (is_array($langs)) {                $this->languages = array_keys($langs);            }        }        $this->grid_column = $this->getGridColumns();    }    /**     * @return array Return array of column for gridViewWidget     */    public function getGridColumns()    {        $columns = [            'checkboxColumn' => [                'width' => '50px',            ],            [                'attribute' => 'message',                'headerOptions'=>['style'=>'max-width: 100px;'],                'contentOptions'=>['style'=>'max-width: 100px;vertical-align:middle;word-wrap:anywhere;white-space:break-spaces'],            ]        ];        foreach ($this->languages as $one) {            $value = $_GET['SourceMessageSearch']['languages'][$one] ?? '';            $columns[] = [                'label' => Yii::t('app', $one),                'value' => 'languages.' . $one,                'format' => 'raw',                'headerOptions'=>['style'=>'max-width: 100px;'],                'contentOptions'=>['style'=>'max-width: 100px;vertical-align:middle;word-wrap:anywhere;white-space:break-spaces'],                'filter' => '<input type="text" value="' . $value . '" class="form-control" name="SourceMessageSearch[languages][' . $one . ']">',            ];        }        $columns[] = [            'class' => 'kartik\grid\ActionColumn',            'width' => "150px",            'viewOptions' => ['class' => 'btn btn-xs btn-outline-info'],            'updateOptions' => ['class' => 'btn btn-xs btn-outline-success'],            'deleteOptions' => ['class' => 'btn btn-xs btn-outline-danger'],        ];        return $columns;    }}