<?php// Translationfunction t($title = "", $category = 'app', $params = [], $language = null){    return Yii::$app->db->cache(function ($db) use ($category, $title, $params, $language) {        return Yii::t($category, $title, $params, $language);    }, 3600 * 24, new \yii\caching\TagDependency(['tags' => ['translate']]));}function settings($section, $key, $default = null){    return Yii::$app->settings->get($section, $key, $default);}function as_integer($value, $options = [], $textOptions = []){    return Yii::$app->formatter->asInteger($value, $options, $textOptions);}	