<?php	use soft\helpers\SUrl;	use soft\helpers\SArray;    /**     * create url     * @see \yii\helpers\Url::to()    */    function to($url = null, $scheme = false)	{		return SUrl::to($url, $scheme);	}    /**     * @see \yii\helpers\ArrayHelper::map()     * @param $array     * @param $from     * @param $to     * @param null $group     * @return array     */    function map($array, $from, $to, $group = null)    {        return SArray::map($array, $from, $to, $group);    }