<?php	// use Yii;	use soft\helpers\SHtml;	use soft\helpers\SArray;	function e($content = "", $doubleEncode = true)    {		    return SHtml::encode($content, $doubleEncode);	}	function a($text, $url = null, $options = [], $icon = null)	{		return SHtml::a($text, $url, $options, $icon);	}	function tag($name, $content = '', $options = [])    {        if ($name === null || $name === false) {            return $content;        }        return SHtml::tag($name, $content, $options);    }    /**     * Icon functions     **/    function fa($icon=''){    	return "<i class='fa fa-{$icon}'></i>";    }    function far($icon=''){    	return "<i class='far fa-{$icon}'></i>";    }    function fas($icon=''){    	return SHtml::tag('i', "", [ 'class' => "fas fa-{$icon}"]);    }    function feather($icon='')    {        return SHtml::tag('i', "", ['data-feather' => $icon]);    }