<?php	function can($role = '')	{		return Yii::$app->user->can($role);	}	function is_guest()    {        return Yii::$app->user->isGuest;    }/** * @param null $attribute * @return false|common\models\User|mixed */function user($attribute = null)    {        $user = Yii::$app->user;        if ($user->isGuest){            return false;        }        if ($attribute == null){            /** @return common\models\User */            return $user->identity;        }        else{            return $user->identity->{$attribute};        }    }