<?php/** * @copyright Copyright &copy; Shukurullo Odilov,  2020 * @package yii2-custom-soft (Odilov) * @version 1.0.0 */namespace soft\grid;use Yii;use kartik\grid\ActionColumn;use yii\helpers\Url;class SActionColumn extends ActionColumn{    /**     * Buttonlar uchun url generatsiya qilishda ishlatish mumkin     * Qiymat sifatida string, array yoki callable function berish mumkin     *     * Usage:     *     * 'urls' =>  [     *      'delete' => function($action, $model, $key, $index, $widget){                return ... ;     *       },     *       'download' => ['download'],     *      ...     * ]     *     * @var array     */    public $urls = [];    /**     * @inheritdoc     */    public $vAlign = 'middle';	public $dropdown = false;    /**     * @inheritDoc     */    public function init()    {        $this->normalizeButtonOptions();        parent::init();    }    /**     * Qo'shimcha ravisgda $this->urls[] parametrini tekshiradi     * Agar shu massiv ichida berilgan $action dagi qiymat topilsa, shu elementning url qimatini qaytaradi     * Aks holda parent methodi ishga tushadi     * @see urls     * @param string $action     * @param \yii\db\ActiveRecordInterface $model     * @param mixed $key     * @param int $index     * @return false|mixed|string     */    public function createUrl($action, $model, $key, $index)	{		if (isset($this->urls[$action]) ) {            if(is_callable($this->urls[$action])){                return call_user_func($this->urls[$action], $action, $model, $key, $index, $this);            }            return Url::to($this->urls[$action]);		}		return parent::createUrl($action, $model, $key, $index);	}    public function normalizeButtonOptions()    {        $this->viewOptions = array_merge($this->defaultViewOptions(), $this->viewOptions);        $this->updateOptions = array_merge($this->defaultUpdateOptions(), $this->updateOptions);        $this->deleteOptions = array_merge($this->defaultDeleteOptions(), $this->deleteOptions);        if (isset($this->deleteOptions['role'])){            $value = $this->deleteOptions['role'];            if ($value == 'modal-remote'){                $this->deleteOptions['data-confirm'] = false;                $this->deleteOptions['data-method'] = false;                $this->deleteOptions['data-request-method'] = 'post';                $this->deleteOptions['data-confirm-title'] = "Tasdiqlaysizmi?";                $this->deleteOptions['data-confirm-message'] = "Siz rostdan ham ushbu elementni o'chirishni xoxlaysizmi?";                Yii::$app->view->registerAjaxCrudAssets();            }            else{                if (empty($value)){                    unset($this->deleteOptions['role']);                }            }        }	}    /**     * View buttoni uchun default holatdagi optionlar     * Bu optionlar $this->viewOptions ga merge qilib yuboriladi     * @see viewOptions     * @return array     */    public function defaultViewOptions()    {        return [            'title'=> "Ko'rish" ,            'label'=> "<i class='fa fa-info-circle'></i> Ko'rish" ,            'data-toggle'=>'tooltip',            'data-pjax' => 0,        ];	}	/**     * Update buttoni uchun default holatdagi optionlar     * Bu optionlar $this->updateOptions ga merge qilib yuboriladi     * @see updateOptions     * @return array     */    public function defaultUpdateOptions()    {        return [            'title'=> "Tahrirlash" ,            'label'=> "<i class='fa fa-edit'></i> Tahrirlash" ,            'data-toggle'=>'tooltip',            'data-pjax' => 0,        ];	}	/**     * Delete buttoni uchun default holatdagi optionlar     * Bu optionlar $this->deleteOptions ga merge qilib yuboriladi     * @see deleteOptions     * @return array     */    public function defaultDeleteOptions()    {        return [            'title'=> "O'chirish",            'data-toggle'=>'tooltip',            'label'=> "<i class='fas fa-trash-alt'></i> O'chirish" ,            'data-pjax' => 0,            'role' => 'modal-remote',            'data-confirm'=> "Siz rostdan ham ushbu elementni o'chirishni xoxlaysizmi?",        ];	}}