<?php	namespace soft\helpers;	use Yii;	use yii\helpers\Url;	use yii\helpers\Html;	class SUrl extends Url	{		/**		 * GridView yoki Listviewda current urldan sorting va pagerlar olib tashlab, qolgan urlni generatsiya qilib beradi		*/		public static function clearUrl($searchClass = null){			$r = Yii::$app->request;			$cid = Yii::$app->controller->id;			$actionid = Yii::$app->controller->action->id;			$params = $r->queryParams;			$route = $cid."/".$actionid;			if (isset($params['page'])) {				unset($params['page']);			}			if (isset($params['per-page'])) {				unset($params['per-page']);			}			if (isset($params['sort'])) {				unset($params['sort']);			}			if (isset($params['_pjax'])) {				unset($params['_pjax']);			}			if ($searchClass != null && isset($params[$searchClass])) {				unset($params[$searchClass]);			}			$params[0] = $route;			return parent::to($params);		        		}		/**		 * Current urldagi lannguage parametrini qiymatini berilgan $language qiymatiga o'zgartiradi		 * */        public static  function currentUrlWithLang($language='')        {            $params = Yii::$app->request->queryParams;            $params['language'] = $language;            $params[0] = "/".Yii::$app->controller->route;            return parent::to($params);		}        public static function homeUrl($params=null)        {                return static::to('/', true);        }    }?>