<?php namespace soft\widget;use soft\base\SWidgetBase;use soft\extra\STemplate;use soft\helpers\SArray;use Yii;	/**	 * @author Shukurullo Odilov	 */	class SBulkButtons extends SWidgetBase	{		public $buttons=[];		public $template = -1;		public $_layout = [		'main' => ' 			<div class="pull-left">				{mainText}:&nbsp;				<i class="far fa-hand-point-right"></i>&nbsp;				{buttons}			</div><div class="clearfix"></div>',];		public $_config = [];		public function main()		{		    if (empty($this->buttons)){		        $this->html = '';		        return ;            }			$items = [];            foreach ($this->buttons as $buttonName => $buttonConfig) {				if ($this->template == -1  || strpos($this->template, "{".$buttonName."}" ) !==  false ) {					$items[$buttonName] = SBulkButton::widget(['config' => $buttonConfig]);				}			}			$buttons = STemplate::widget([				'template' => $this->template,				'items' => $items,				'separator' => ' ',			]);			$this->html = strtr($this->_layout['main'], [				'{mainText}' =>  SArray::getValue($this->_config, 'mainText', Yii::t('app', 'With selected')),				'{buttons}' => $buttons,			]);		}	}	?>